import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class SweepLineAnimator extends StatefulWidget {  final Widget child;  final Color lineColor;  final double lineHeight;  final Duration duration;  const SweepLineAnimator({    super.key,    required this.child,    this.lineColor = Colors.greenAccent,    this.lineHeight = 4.0,    this.duration = const Duration(seconds: 2),  });  @override  State<SweepLineAnimator> createState() => _SweepLineAnimatorState();}class _SweepLineAnimatorState extends State<SweepLineAnimator>    with SingleTickerProviderStateMixin {  late AnimationController _controller;  late Animation<double> _sweepAnim;  @override  void initState() {    super.initState();    _controller = AnimationController(      duration: widget.duration,      vsync: this,    );    _sweepAnim = Tween<double>(begin: 0.0, end: 1.0).animate(      CurvedAnimation(parent: _controller, curve: Curves.easeInOut),    );    _controller.repeat(); // Continuous loop  }  @override  void dispose() {    _controller.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return AnimatedBuilder(      animation: _sweepAnim,      builder: (context, child) {        return Stack(          children: [            widget.child, // Original container            // Sweep line overlay            Positioned(              top: 0,              left: 0,              right: 0,              height: widget.lineHeight,              child: ClipRect(                child: OverflowBox(                  maxWidth: double.infinity,                  child: Align(                    alignment: Alignment.centerLeft,                    widthFactor: _sweepAnim.value,                    child: Container(                      height: widget.lineHeight,                      decoration: BoxDecoration(                        gradient: LinearGradient(                          colors: [                            widget.lineColor.withOpacity(0.0),                            widget.lineColor,                            widget.lineColor.withOpacity(0.8),                            widget.lineColor.withOpacity(0.0),                          ],                        ),                      ),                    ),                  ),                ),              ),            ),          ],        );      },    );  }}