import 'package:flutter/material.dart';class ContactSection extends StatelessWidget {  const ContactSection({super.key});  @override  Widget build(BuildContext context) {    return Container(      width: double.infinity,      padding: const EdgeInsets.symmetric(vertical: 80, horizontal: 24),      child: Center(        child: Container(          width: 600,          padding: const EdgeInsets.all(40),          decoration: BoxDecoration(            color: Colors.white.withOpacity(0.05),            borderRadius: BorderRadius.circular(32),            border: Border.all(color: Colors.white12),          ),          child: Column(            mainAxisSize: MainAxisSize.min,            children: [              const Text(                'Contact Me',                style: TextStyle(                  color: Colors.white,                  fontSize: 32,                  fontWeight: FontWeight.bold,                ),              ),              const SizedBox(height: 40),              const CircleAvatar(                radius: 60,                backgroundColor: Colors.blueAccent,                child: Icon(Icons.mail_outline, size: 60, color: Colors.white),              ),              const SizedBox(height: 40),              _buildContactRow(Icons.email, 'Email', 'john.doe@example.com'),              _buildDivider(),              _buildContactRow(Icons.phone, 'Phone', '+1 234 567 890'),              _buildDivider(),              _buildContactRow(Icons.location_on, 'Location', 'San Francisco, CA'),              _buildDivider(),              _buildContactRow(Icons.language, 'Website', 'www.johndoe.dev'),            ],          ),        ),      ),    );  }  Widget _buildContactRow(IconData icon, String label, String value) {    return Padding(      padding: const EdgeInsets.symmetric(vertical: 16.0),      child: Row(        children: [          Icon(icon, color: Colors.blueAccent, size: 24),          const SizedBox(width: 20),          Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              Text(                label,                style: TextStyle(color: Colors.white.withOpacity(0.6), fontSize: 14),              ),              Text(                value,                style: const TextStyle(                  color: Colors.white,                  fontSize: 18,                  fontWeight: FontWeight.bold,                ),              ),            ],          ),        ],      ),    );  }  Widget _buildDivider() {    return Divider(color: Colors.white.withOpacity(0.1));  }}