import 'package:flutter/material.dart';class UserDetailsSection extends StatelessWidget {  const UserDetailsSection({super.key});  @override  Widget build(BuildContext context) {    return Container(      width: double.infinity,      padding: const EdgeInsets.symmetric(vertical: 80, horizontal: 24),      child: Center(        child: Container(          width: 600,          padding: const EdgeInsets.all(40),          decoration: BoxDecoration(            color: Colors.white.withOpacity(0.05),            borderRadius: BorderRadius.circular(32),            border: Border.all(color: Colors.white12),          ),          child: Column(            mainAxisSize: MainAxisSize.min,            children: [              const Text(                'User Profile',                style: TextStyle(                  color: Colors.white,                  fontSize: 32,                  fontWeight: FontWeight.bold,                ),              ),              const SizedBox(height: 40),              const CircleAvatar(                radius: 60,                backgroundColor: Color(0xFF22C55E),                child: Icon(Icons.person, size: 60, color: Colors.white),              ),              const SizedBox(height: 40),              _buildDetailRow('Name', 'John Doe'),              _buildDetailDivider(),              _buildDetailRow('Age', '28'),              _buildDetailDivider(),              _buildDetailRow('Designation', 'Flutter Developer'),              _buildDetailDivider(),              _buildDetailRow('Height', '5\'9"'),            ],          ),        ),      ),    );  }  Widget _buildDetailRow(String label, String value) {    return Padding(      padding: const EdgeInsets.symmetric(vertical: 16.0),      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        children: [          Text(            label,            style: TextStyle(color: Colors.white.withOpacity(0.6), fontSize: 18),          ),          Text(            value,            style: const TextStyle(              color: Colors.white,              fontSize: 20,              fontWeight: FontWeight.bold,            ),          ),        ],      ),    );  }  Widget _buildDetailDivider() {    return Divider(color: Colors.white.withOpacity(0.1));  }}